import { HttpClient } from '@angular/common/http';
import { ThisReceiver } from '@angular/compiler';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { User } from 'src/app/Model/User/user';

@Component({
  selector: 'app-editprofile',
  templateUrl: './editprofile.component.html',
  styleUrls: ['./editprofile.component.css']
})
export class EditprofileComponent implements OnInit {

  user: User
  constructor(private http: HttpClient, private activatedRoute: ActivatedRoute) {
    this.user = new User()
  }

  ngOnInit(): void {
    const id = this.activatedRoute.snapshot.params[''];
    const email = localStorage.getItem('UserEmail')
    this.GetUserByEmail(email)
  }


  GetUserByEmail(email: string | null) {
    this.http.get<User>("https://localhost:44325/api/User/GetUserByEmail?email=" + email).subscribe(result => {
      console.warn(result)
      this.user = result
    })
  }

  Editprofile() {
    this.http.put<boolean>("https://localhost:44325/api/User/EditUser=" + this.user.user_Id, this.user).subscribe(result => {
      console.warn(result)
    })
  }
}
